import React from "react";
import axios from "axios";
import faker from "faker";
import { Button, Form, TextArea } from "semantic-ui-react";

class AddReview extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      author: "",
      avatar: "",
      rating: 0,
      rate: 0,
      review: ""
    };
  }

  onChangeAuthor = e => {
    this.setState({
      author: e.target.value
    });
  };

  onChangeRating = e => {
    this.setState({
      rating: e.target.value
    });
  };

  onChangeRate = e => {
    this.setState({
      rate: e.target.value
    });
  };

  onChangeReview = e => {
    this.setState({
      review: e.target.value
    });
  };

  onSubmit = e => {
    e.preventDefault();

    const newReview = {
      reviews: {
        author: this.state.author,
        avatar: faker.image.avatar(),
        rating: this.state.rating,
        rate: this.state.rate,
        review: this.state.review
      }
    };

    axios
      .post(
        "http://localhost:4000/instructors/" + this.props.match.params.id,
        newReview
      )
      .then(res => {
        console.log("axios res.data newReview:", res.data);
      });
  };

  render() {
    return (
      <Form onSubmit={this.onSubmit}>
        <Form.Field>
          <label>Name:</label>
          <input
            value={this.state.name}
            onChange={this.state.onChangeName}
            placeholder="Name"
          />
        </Form.Field>
        <Form.Field>
          <label>Rating:</label>
          <input
            value={this.state.rating}
            onChange={this.state.onChangeRating}
            placeholder="Rating"
          />
        </Form.Field>
        <Form.Field>
          <label>Hourly Rate:</label>
          <input
            value={this.state.rate}
            onChange={this.state.onChangeRate}
            placeholder="Hourly Rate"
          />
        </Form.Field>
        <Form.Field
          value={this.state.review}
          onChange={this.state.onChangeReview}
          control={TextArea}
          label="Review"
          placeholder="Tell us about your experience..."
        />
        <Button type="submit">Submit</Button>
      </Form>
    );
  }
}

export default AddReview;
